
public class DeclaracionVariables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Se pueden almacenar varias edades en diferentes variables
		
		//int edadMaria, edadPedro, edadLuis;
		
		byte edad; //Declaracion de variable
		
		edad=27; //Inicializacion de variable
		
		edad=35; //El valor que almacena una variable puede variar. Ya que el Flujo del programa es de arriba hacia abajo.
		
		System.out.println(edad); //Al seguir el flujo a esta altura imprimira el valor de la variable edad que es 35.

	}

}
