package Ejercicios;

public class Ejercicio2_Calculo_Raiz {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int numero; //Declaracion
		
		numero = 9; //Iniciacion
		
		int resultado;
		
		resultado =(int)Math.sqrt(numero);//Casting de un parametro double a tipo int.
		
		System.out.println("La raiz cuadrada de " + numero + " es " + resultado);

	}

}
